using System.ComponentModel;
using Microsoft.AspNetCore.Components.Web;
using Spellslinger.Models;

namespace Spellslinger.Services;

/// <summary>
/// The main game state.
/// </summary>
public interface IGame
{
	/// <summary>
	/// The map generator for the game.
	/// </summary>
	public IMapGenerator MapGenerator { get; }

	/// <summary>
	/// The map on which the player is curerntly located.
	/// </summary>
	public Map? CurrentMap { get; set; }

	/// <summary>
	/// The log of messages generated by the game.
	/// </summary>
	public IList<string> Log { get; }

	/// <summary>
	/// The player character, or null if the player is dead.
	/// </summary>
	public Actor? Player { get; set; }

	/// <summary>
	/// A random number generator.
	/// </summary>
	public Random Rng { get; }

	/// <summary>
	/// The keys that can be used to cast spells.
	/// </summary>
	public string SpellKeys { get; }

	/// <summary>
	/// The current input mode for the UI.
	/// </summary>
	public InputMode InputMode { get; set; }

	/// <summary>
	/// The spell that has been input, and is pending a direction.
	/// </summary>
	public Spell? InputSpell { get; set; }

	/// <summary>
	/// A shop that the player can visit in the town.
	/// </summary>
	public Shop Shop { get; }

	/// <summary>
	/// Controls the game's music.
	/// </summary>
	public Music Music { get; }

	/// <summary>
	/// Is the endgame artifact collected?
	/// </summary>
	public bool IsArtifactCollected { get; set; }

	/// <summary>
	/// Sends keyboard input to the game.
	/// </summary>
	/// <param name="e"></param>
	public void AcceptKeyboardInput(KeyboardEventArgs e);

	/// <summary>
	/// Notifies listeners of an update to the game state.
	/// </summary>
	public void Update();

	/// <summary>
	/// Event that is raised when the input mode is changed.
	/// </summary>
	public event EventHandler<InputMode> InputModeChanged;

	/// <summary>
	/// Event that is raised when the game state is updated.
	/// </summary>
	public event EventHandler Updated;
}
